mapper.insert:


  SELECT LAST_INSERT_ID()

insert into message (m_title, m_question, m_answer, 
  q_user_id, a_user_id, q_user_name, 
  a_user_name, q_time, a_time
  )
values (#{mTitle,jdbcType=VARCHAR}, #{mQuestion,jdbcType=VARCHAR}, #{mAnswer,jdbcType=VARCHAR}, 
  #{qUserId,jdbcType=BIGINT}, #{aUserId,jdbcType=BIGINT}, #{qUserName,jdbcType=VARCHAR}, 
  #{aUserName,jdbcType=VARCHAR}, #{qTime,jdbcType=TIMESTAMP}, #{aTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select m_id, m_title, m_question, m_answer, q_user_id, a_user_id, q_user_name, a_user_name,
q_time, a_time
from message where q_user_id=#{UID}
