mapper.delete:

delete from reration_info
where u_id = #{uId,jdbcType=BIGINT}
  and r_u_id = #{rUId,jdbcType=BIGINT}

mapper.update:

update reration_info
set `status` = #{status,jdbcType=TINYINT},
  creat_time = #{creatTime,jdbcType=TIMESTAMP},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where u_id = #{uId,jdbcType=BIGINT}
  and r_u_id = #{rUId,jdbcType=BIGINT}

mapper.select:

select u_id, r_u_id, `status`, creat_time, modify_time
from reration_info
where u_id = #{uId,jdbcType=BIGINT}
  and r_u_id = #{rUId,jdbcType=BIGINT}
