mapper.insert:


  SELECT LAST_INSERT_ID()

insert into user (`password`, u_no, user_name, 
  email, phone, sex, 
  address, `identity`, `power`
  )
values (#{password,jdbcType=VARCHAR}, #{uNo,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{sex,jdbcType=TINYINT}, 
  #{address,jdbcType=VARCHAR}, #{identity,jdbcType=VARCHAR},#{power,jdbcType=INTEGER}
  )

mapper.select:

select u_id, `password`, u_no, user_name, email, phone, sex, address, `identity`,
`status`, `power`, creat_time, modify_time
from user where `power`=#{power}


select u_id, `password`, u_no, user_name, email, phone, sex, address, `identity`,
`status`, `power`, creat_time, modify_time
from user  where 1=1

 AND user_name=#{Uname}



  AND  `identity`=#{Uidentity}
